
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import "./InfoBox.css";
import AcUnitIcon from '@mui/icons-material/AcUnit';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import ThunderstormIcon from '@mui/icons-material/Thunderstorm';

export default function InfoBox({info}){

    const INIT_URL="https://media.gettyimages.com/id/1181240920/photo/hot-arid-climate-landscape.jpg?s=612x612&w=gi&k=20&c=xfe0407EjPCL9JAdzsLzWZSoo0UmMwv5t8bHPbmeUNQ="
    
    const HOT_URL="data:image/jpeg;base64,";
    const COLD_URL="https://images.unsplash.com/photo-1612208695882-02f2322b7fee?fm=jpg&w=3000&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8Y29sZCUyMHdlYXRoZXJ8ZW58MHx8MHx8fDA%3D";
    const RAIN_URL="data:image/jpeg;base64,";


    return(
        <div className="InfoBox">

        <div className="cardcontainer">

        <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        sx={{ height: 140 }}
        image={info.humidity>80? RAIN_URL:info.temp>15?HOT_URL:COLD_URL}
        title="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
         {info.city}{
            info.humidity>80
                ? <ThunderstormIcon/>
                :info.temp>15
                ?<WbSunnyIcon/>
                :<AcUnitIcon/>
         }
        </Typography>
        <Typography variant="body2" color="text.secondary" component={"span"}>
          <p>Temperature={info.temp}&deg;C</p>
          <p>Humidity={info.humidity}</p>
          <p>Min Temp={info.tempMin}&deg;C</p>
          <p>Max Temp={info.tempMax}&deg;C</p>
          <p>
            Weather can be descirbed as <i>{info.temp}</i> and feels like{info.feelslike}&deg;C
            </p>
        </Typography>
      </CardContent>
     
    </Card>
    </div>
        </div>
    );
}